INSERT INTO `post` VALUES (16,1,'whatis',1,'[\"cms\"]','23847154_10214223072746389_5877820337674677612_o.jpg','What is?',NULL,0,2,1,NULL,'<p><span style=\"background-color: rgb(255, 255, 0);\">GaiaCMS&nbsp;</span>is the base system of web-development.\n       In its starting point GaiaCMS offers:&nbsp;\n       a) Template creation by breaking page to widgets and widgetized areas.&nbsp;\n       b) Integration of static html templates to a dynamic web application. The idea of this CMS is that breaking the big picture of a website into smaller reusable units, makes it really fast to setup&nbsp; and maintain.</p><p> &nbsp; &nbsp; &nbsp;<code>Features Active</code></p><ol>\n    <li> Create and customize a GaiaCMS template.</li>\n    <li> Integrate GaiaCMS&nbsp;to an html template.</li>\n    <li> Posts with Taxonomies and custom taxonomy groups.</li>\n    <li> Users with roles, permissions and custom usergroups.</li>\n    <li> Create Pages and Widgets with custom content.</li>\n    <li> Gaia.js javascript library.</li></ol><ol>\n    </ol><p></p>',NULL,NULL,0,1576259353,0);
